package app.coinfo.library.database.entity

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.PrimaryKey

/** The database table which keeps information about transaction */
@Entity(tableName = "transactions")
internal data class Transaction(
    /** Autogenerated transaction ID */
    @PrimaryKey(autoGenerate = true) val transactionId: Long = 0L,

    /** The ID of the coin to which the transaction belongs */
    @ColumnInfo(name = "coinCreatorId") val coinId: String,

    /** The ID of the portfolio to which transaction belongs */
    @ColumnInfo(name = "portfolioCreatorId") val portfolioId: Long,

    /** The transaction date */
    @ColumnInfo(name = "date") val date: Long,

    /** The transaction amount */
    @ColumnInfo(name = "amount") val amount: Double,

    /** The total price for given amount. */
    @ColumnInfo(name = "price") val price: Double,

    /** The currency in which the coin was purchased. */
    @ColumnInfo(name = "currency") val currency: String,

    /** The type of the transaction: Buy, Sell, Transfer */
    @ColumnInfo(name = "type") val type: Int,

    /** Fee for transaction */
    @ColumnInfo(name = "fee") val fee: Double,

    /** Note associated with transaction */
    @ColumnInfo(name = "note") val note: String
)
